INSERT INTO `sys_intervene_analysis` VALUES ('1', '摄入总量不足', '食物摄入总量不足，身体会处于负平衡状态，长期摄入不足会出现身体消瘦、免疫力下降等状况，所以您需要保证每天食物摄入总量充足。', '9', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('2', '摄入总量超标', '食物摄入总量超标，长此以往多余的脂肪会贮存起来使您的身体变胖，而肥胖又是导致高血压、高血糖等慢性疾病“元凶”，为了您的健康，你需要减少每天食物的摄入量。', '9', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('3', '摄入总量超标+超重、肥胖', '您的超重与肥胖与食物摄入过多直接相关，而身体超重或肥胖又被称为“万病之源”，所以为了您的健康一定要控制饮食。', '9', '2', '23');
INSERT INTO `sys_intervene_analysis` VALUES ('4', '摄入总量正常', '您的摄入量正常，请保持。', '9', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('5', '主食不足', '长期主食摄入不足可出现乏力、头晕等症状，这是因为我们的身体主要是通过主食提供能量的，特别是为肌肉和大脑提供能量。所以主食不能吃得太少。', '1', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('6', '主食超标', '主食中多含有碳水化合物。长期主食摄入超标，体内多余的碳水化合物会转化为脂肪，进而引起肥胖。所以每天主食不能吃太多，您一定要控制好主食的量。', '1', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('7', '主食超标+糖尿病', '主食超标会使餐后血糖迅速升高，血糖波动过大，所以您一定要控制主食的量，而且做到粗细搭配。', '1', '2', '22');
INSERT INTO `sys_intervene_analysis` VALUES ('8', '主食正常', '您的摄入量正常，请保持。', '1', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('9', '蔬菜不足', '蔬菜摄入不足会导致体内膳食纤维缺乏，体内毒素无法及时排出，长期下来毒素进入血液循环，就会引发头痛、皮肤粗糙、发痘痘等。所以您需要每天吃够一斤蔬菜。', '2', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('10', '蔬菜不足+糖尿病', '蔬菜中的膳食纤维以纤维素、半纤维素、果胶等为主，它们在胃内停留时间长，还会干扰葡萄糖的吸收，从而具有降低餐后血糖和胰岛素水平的作用。所以您要多食蔬菜哦。', '2', '0', '22');
INSERT INTO `sys_intervene_analysis` VALUES ('11', '蔬菜超标', '长期蔬菜摄入超标，膳食纤维过多摄入，不光会造成钙的流失，而且还会使蛋白质、铁、维生素等其他营养素流失。所以蔬菜虽好，但也不是越多越好。', '2', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('12', '蔬菜正常', '您的摄入量正常，请保持。', '2', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('13', '肉蛋鱼不足', '肉蛋鱼可提供人体所必需的优质蛋白质。若长期肉蛋鱼摄入不足，就会导致蛋白质缺乏，进而出现水肿、生长迟缓、皮肤色素沉着等现象，所以您肉蛋鱼不能吃得太少。', '3', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('14', '肉蛋鱼超标', '肉蛋鱼中富含优质蛋白质，长期肉蛋鱼摄入超标，摄入过多的蛋白质会引起胃肠道功能紊乱，导致肝、肾负荷增加。所以您需要保证每天肉蛋鱼摄入量达标。', '3', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('15', '肉蛋鱼超标+血脂异常', '血脂异常的患者要尽量少吃或不吃肥肉，另外鸡蛋蛋黄含有丰富的胆固醇，所以高胆固醇血症的病人应适量限制蛋黄的摄入，一个星期之内可以隔天吃一个鸡蛋。', '3', '2', '24');
INSERT INTO `sys_intervene_analysis` VALUES ('16', '肉蛋鱼正常', '您的摄入量正常，请保持。', '3', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('17', '水果不足', '水果中富含多种维生素，长期水果摄入不足，就会因为体内缺乏维生素而出现肌体代谢失去平衡，免疫力下降等现象。所以您需要每天都吃水果哦。', '5', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('18', '水果不足+糖尿病', '糖尿病人是可以吃水果的，第一要选择低糖的水果如梨、青瓜、苹果、橙子、柚子、桃、橘子等，第二要在两餐之间食用，并且适当减少主食的摄入量。', '5', '0', '22');
INSERT INTO `sys_intervene_analysis` VALUES ('19', '水果超标', '长期用水果当正餐，肯定会引起蛋白质和铁的摄入不足，从而引起贫血、免疫功能降低等现象，损害健康得不偿失，所以水果只能是我们饮食中的“配角”。', '5', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('20', '水果正常', '您的摄入量正常，请保持。', '5', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('21', '乳豆不足', '乳类及豆制品可提供人体所必需的蛋白质、B族维生素、以及丰富的钙质。牛奶或其他奶制品的食用，对骨健康、防治中风、心血管疾病、肠癌颇有益处，每天一杯奶身体更健康。', '4', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('22', '乳豆超标', '据研究发现，大量饮用牛奶可能会增加前列腺癌、帕金森等疾病的发病风险，而且还会加重胃肠负担。每天一杯奶对您身体更健康。', '4', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('23', '乳豆正常', '您的摄入量正常，请保持。', '4', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('24', '油脂不足', '油脂的主要作用是贮存和供应热量，为人体提供能量。若长期油脂摄入不足，人会消瘦、体抗力下降，而且还会因能量缺乏而产生疾病，所以您需要保证油脂摄入充足。', '6', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('25', '油脂超标', '油脂含脂肪、热量过高，摄入超标时易引起肥胖。若长期油脂摄入超标，就会降低肠胃功能、伤害肝脏、促进衰老以及导致心血管疾病的发生等。所以您需要保证油脂摄入达标。', '6', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('26', '油脂超标+血脂异常', '血脂异常患者要严格限制油脂类食物的摄入，比如坚果、色拉酱、糕点、快餐食品等，经常进食这些食物会导致能量超标，不利于血脂的控制。', '6', '2', '24');
INSERT INTO `sys_intervene_analysis` VALUES ('27', '油脂正常', '您的摄入量正常，请保持。', '6', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('28', '饮水量不足', '水是人体的润滑油，它能使关节、脏器及组织细胞减少相互间的摩擦，保持“和睦相处”的状态。人体在通过水来吸收各种各样营养物质的同时，亦借助它来排泄运送代谢物。每天多喝水，是维持健康的一大良方。', '8', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('29', '饮水量不足+糖尿病', '糖尿病人要多饮水，实际是对体内失水的补充，而且还有改善血液、促进循环、增加代谢及消除酮体等作用，是对人体失水的一种保护性反应。因此糖尿病人不但不能限制饮水，还应适当多喝水。', '8', '0', '22');
INSERT INTO `sys_intervene_analysis` VALUES ('30', '饮水正常', '您的摄入量正常，请保持。', '8', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('31', '食盐超标', '长期食盐摄入超标，会使体内的钠大量积聚，而过多的钠可降低钙在骨骼中的储留，使骨密度下降，并会导致高血压等多种心脑血管疾病。所以您宜清淡饮食，不宜吃太咸。', '7', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('32', '食盐超标+高血压', '经研究表明，盐的摄入与高血压呈正比，即人体摄取盐量越多血压水平就越高，日均摄盐量每增加1克，平均血压上升2毫米汞柱，低压上升1.7毫米汞柱。所以建议高血压患者每日摄盐量小于6克。', '7', '2', '27');
INSERT INTO `sys_intervene_analysis` VALUES ('33', '食盐正常', '您的摄入量正常，请保持。', '7', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('34', '运动不足', '您此阶段运动量不足,需要合理的加强运动。', '10', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('35', '运动达标', '您此阶段运动量合理，请继续保持。', '10', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('36', '运动超标', '您此阶段运动量过大，请适量加餐，以保证充足体力。', '10', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('37', '睡眠不足', '您此阶段睡眠不足,长期的睡眠不足会使身体免疫力下降引发多种疾病，请在晚上10点左右的“最佳入睡时间”上床休息并保证每天睡眠时间大于7小时。', '11', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('38', '睡眠达标', '您此阶段睡眠充足，请继续保持。', '11', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('39', '睡眠过长', '您此阶段睡眠过多，会使神经中枢长期处于抑制状态，起床后往往感到昏沉和无精打采，所以请保证每日7-9小时的睡眠时间即可。', '11', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('40', '收缩压高', '收缩压持续升高的人群，其心血管发病危险是正常人群的2-10倍，血压越高，几率越大。干预治疗可使脑卒中发生危险下降31%。', '12', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('41', '收缩压低', ' 收缩压过低可影响身体各重要脏器的血流灌注，会增加心脑肾等靶器官损害。', '12', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('42', '收缩压正常偏高', '血压处于正常偏高水平如不注意调理，则可能会继续增高，我国10组人群研究表明，收缩压每升高10mmHg，脑卒中发生相对危险增加49%。', '12', '4', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('43', '收缩压正常', '您的收缩压处于正常水平，请继续保持。', '12', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('44', '舒张压高', '舒张压持续升高可增加患脑卒中等心脑血管疾病的几率，血压越高，发病几率越大。', '13', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('45', '舒张压低', '舒张压长期偏低会大大增加老年性痴呆的发生率。', '13', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('46', '舒张压正常偏高', '舒张压处于正常偏高时，应注意监测，防治继续其增高，舒张压每升高5mmHg，脑卒中危险增加46%。', '13', '4', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('47', '舒张压正常', '您的舒张压属于正常，请继续保持。', '13', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('48', '空腹血糖高', '长期血糖增高会使全身各个组织器官发生病变，导致急慢性疾病或并发症的发生。', '14', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('49', '空腹血糖正常偏高', '空腹血糖若持续处于正常偏高范围，则有可能属于糖调节异常，而糖调节异常标志着将来发生大血管病、糖尿病、微血管病,以及肿瘤和痴呆等疾病的危险性增高。', '14', '4', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('50', '空腹血糖低', '血糖低在一定时间内如果不能及时做出正确的诊断和处理,可发生脑水肿，缺血性点状坏死，脑软化，痴呆，昏迷，休克甚至死亡。', '14', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('51', '空腹血糖正常偏低', '空腹血糖处于正常偏低水平，极易出现低血糖，注意定期监测。', '14', '3', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('52', '空腹血糖正常', '您的空腹血糖处于正常范围，请继续保持。', '14', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('53', '餐后血糖高', '长期血糖过高可破坏人体内环境，引起一系列生化病理改变，影响各个脏器的功能。应当在医生的指导下，积极主动地将血糖稳定在正常水平，防止或延缓并发症的发生。', '15', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('54', '餐后血糖正常偏高', '餐后2小时血糖若持续处于正常偏高范围，有发展为糖耐量减低的可能；每年有1.5%-10.0%的糖耐量减低患者发展为2型糖尿病。而生活方式干预3年可使糖耐量减低发展为2型糖尿病的风险下降58%。', '15', '4', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('55', '餐后血糖低', '反复低血糖容易导致记忆力下降，增加老年痴呆的发生几率，同时会减少低血糖的报警症状，从而延误治疗时机。', '15', '0', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('56', '餐后血糖正常偏低', '空腹血糖处于正常偏低水平，极易出现低血糖，注意定期监测。', '15', '3', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('57', '餐后血糖正常', '您的餐后2小时血糖处于正常范围，请继续保持。', '15', '1', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('58', 'BMI肥胖', '肥胖人群患高血压、糖尿病等慢病的风险是体重正常者的3-4倍。其中90%者已经患有高血压等疾病。', '16', '2', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('59', 'BMI超重', '超重人群患高血压的危险是体重正常者的3~4倍，患糖尿病的危险是体重正常者的2~3倍。', '16', '4', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('60', 'BMI消瘦', '体质消瘦多存在营养不良等症，体质消瘦不仅容易疲倦、体力差，而且易造成免疫力下降、耐寒抗病能力弱，易患多种疾病。', '16', '3', '0');
INSERT INTO `sys_intervene_analysis` VALUES ('61', 'BMI正常', '您的体质指数属于正常范围，请继续保持。', '16', '1', '0');
